const Card = ({title, content, image, show}) => {
    return (
            show && (
                <div className="inline-flex cursor-pointer group rounded-md border-solid border-1 bg-zinc-300 h-72 w-72 border-zinc-800 border-2  m-1">
                    <div className="bg-zinc-800 text-2xl place-items-center mb-5 p-3 text-zinc-50 opacity-80">{title}</div>
                    <div className="w-full h-full -mt-5 object-fill select-none"><img src={image} /></div>
                    <div className="grid place-items-center opacity-0 duration:40 group-hover:opacity-70 group-hover:bg-zinc-50 group-hover:text-zinc-800 text-lg">{content}</div>
                </div>
            )
    )
}

export default Card;
