const Card = ({title, content, image, show}) => {
    return (
            show && (
                <div className="rounded-md border-solid border-1 bg-zinc-300 h-1/4 w-80 border-zinc-800 border-2  m-1">
                    <div className="bg-zinc-800 text-2xl grid place-items-center mb-5 text-zinc-50 opacity-80">{title}</div>
                    <img className="max-w-xs max-h-xs object-fill" src={image} />
                    <p className="bg">{content}</p>
                </div>
            )
    )
}

export default Card;
