const Container = ({title, content, show, children}) => {
    return (
            show && (<div className="rounded-md border-emerald-500 border-solid border-1 bg-emerald-50 h-auto w-11/12% m-1 p-8">
               <h3>{title}</h3>
                <p>{content}</p>
                <div>{children}</div>
            </div>)
    )
}

export default Container;
