const Card = ({title, content, image, show, onClick}) => {
    return (
            show && (

                <div 
                    onClick={onClick}
                    className="group rounded-xl flex items-center justify-center m-6">
                    <figure className="relative max-w-xs cursor-pointer">
                        <img className="rounded-lg" src={image}/>
                        <figcaption className="absolute text-md -mt-64 text-white px-4">
                            <div className="grid place-items-center text-xl">
                                <h1 className="group-hover:hidden">{title}</h1>
                                <h1 className="hidden group-hover:block group-hover:bg-slate-800 group-hover:opacity-80 p-1 rounded-lg">Click to Learn More</h1>
                            </div>
                            <div className="max-h-48 opacity-0 p-2 rounded-xl group-hover:opacity-80 group-hover:bg-slate-800">
                                <h1>{content}</h1>
                            </div>
                        </figcaption>
                    </figure>
                </div>
            )
    )
}

export default Card;
